import {
  onSnapshot,
  collection,
  getDoc,
  addDoc,
  updateDoc,
  deleteDoc,
  where,
  doc,
  query,
  getDocs
} from "firebase/firestore";
import Tasks from "./task";
import "./dash.css";

import { db } from "../../firebase.js";




const deleteChangeHandler = async (id: any) => {
  const docRef = doc(db, "tasks", id);
  await deleteDoc(docRef);
  // INITIAL_TODO = INITIAL_TODO.filter((item) => item.task !== deleteTask);
  // setNewTodo(INITIAL_TODO);
};
const projdeleteChangeHandler = async (id: any) => {
  const docRef = doc(db, "projects", id);
  await deleteDoc(docRef);
  // INITIAL_TODO = INITIAL_TODO.filter((item) => item.task !== deleteTask);
  // setNewTodo(INITIAL_TODO);
};

const Project: React.FC<{
  onDelete: Function;
}> = () => {
  const projdeleteHandler = () => {
    projects.onDelete(projects.id);
  };

  // const [task, settask] = useState("");
  const [projcolor, setprojcolor] = useState("");

  const [tasks, settasks] = useState([]);
  const taskInputRef = useRef<HTMlInputElement>(null);
  const taskCollectionRef = collection(db, "tasks");

  // useEffect(
  //   () =>
  //     onSnapshot(collection(db, "tasks"), where("projid", "==", id), function (
  //       snapshot
  //     ): void {
  //       return settasks(
  //         snapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }))
  //       );
  //     }),
  //   []
  // );
  const [showActionSheet, setShowActionSheet] = useState(false);
  const pushtask = async () => {


    const msg = project?.color;
    // console.log(msg);
    await addDoc(taskCollectionRef, {
      task: task,
      projid: id,
      projcolor: msg
    });
  };

  const [isOpen, setIsOpen] = useState(false);
  const [projects, setProjects] = useState([] );
  const params = useParams();
  const id = params?.id;

  const [project, setProject] = useState();
  const myfun = async () => {
    const docRef = await doc(db, "projects", id);
    const docSnap = await getDoc(docRef);
    setProject(docSnap.data());
    console.log(id);
    const q = query(collection(db, "tasks"), where("projid", "==", id));
    const querySnapshot = await getDocs(q);
    console.log("mee", tasks);
    var myTasks = [];
    querySnapshot.forEach((doc) => {
      // console.log(doc.id, " => ", doc.data());
      myTasks.push(doc.data());
    });
    settasks(myTasks);
  };
  console.log(tasks);
  const deleteproj = async () => {
    const docRef = doc(db, "projects", id);
    await deleteDoc(docRef);
    setProject(docSnap.data());
  };

  useEffect(() => {
    myfun();
  }, [id]);
  // console.log(project);
  // console.log("id:", id);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>
            <IonButton routerLink="/tab3">Back</IonButton>
            <IonButton
              onClick={() => setShowActionSheet(true)}
              expand="block"
              className="trashright"
            >
              {" "}
              <IonIcon className="trash" icon={trash} />
            </IonButton>
          </IonTitle>
          <IonActionSheet
            isOpen={showActionSheet}
            onDidDismiss={() => setShowActionSheet(false)}
            cssClass="my-custom-class"
            buttons={[
              {
                text: "Delete",
                role: "destructive",
                icon: trash,
                id: "delete-button",
                data: {
                  type: "delete"
                },
                handler: async () => {
                  console.log("Delete clicked");
                  await deleteDoc(doc(db, "projects", id));
                }
              }
            ]}
          ></IonActionSheet>
        </IonToolbar>
      </IonHeader>
      <IonFab
        vertical="bottom"
        horizontal="center"
        slot="fixed"
        className="ion-padding"
      >
        <IonFabButton onClick={() => setIsOpen(true)}>
          <IonIcon icon={addOutline} />
        </IonFabButton>
      </IonFab>

      <IonContent>
        <br />

        <IonContent className="ion-padding">
          <IonTitle className="iontitle">{project?.projtitle}</IonTitle>
          <br />
          <IonLabel>Objectif :</IonLabel>
          <br />
          <br />
          <IonItem> {project?.des} </IonItem>
          <br />
          <IonTitle>Task To Do</IonTitle>

          <div>
            {tasks.map((tasks: any) => (
              <div key={tasks.id}>
                <Tasks
                  id={tasks.id}
                  task={tasks.task}
                  projid={tasks.projid}
                  projcolor={tasks.projcolor}
                  onDelete={deleteChangeHandler}
                />
              </div>
            ))}
          </div>
        </IonContent>

        <IonModal isOpen={isOpen}>
          <IonHeader>
            <IonToolbar>
              <IonTitle>{project?.projtitle}</IonTitle>
              <IonButtons slot="end">
                <IonButton onClick={() => setIsOpen(false)}>Close</IonButton>
              </IonButtons>
            </IonToolbar>
          </IonHeader>
          <IonContent className="ion-padding">
            <IonTitle>Add task {project?.color}</IonTitle>
            <IonItem>
              <IonLabel position="floating">Task</IonLabel>
              <IonInput
                ref={taskInputRef}
                onIonChange={(e: any) => settask(e.target.value)}
              />
            </IonItem>
            <IonButton
              onClick={pushtask}
              className="ion-margin-top"
              expand="block"
            >
              Add
            </IonButton>
          </IonContent>
        </IonModal>
      </IonContent>
    </IonPage>
  );
};

export default Project;
