  onSnapshot,
  collection,
  getDoc,
  addDoc,
  updateDoc,
  deleteDoc,
  where,
  doc,
  query,
  getDocs
} from "firebase/firestore";
import Tasks from "./task";
import "./dash.css";

import { db } from "../../firebase.js";




const deleteChangeHandler = async (id: any) => {
