  };

  useEffect(() => {
    myfun();
  }, [id]);
  // console.log(project);
  // console.log("id:", id);

  return (
    <div>
      <div>
        <div>



        </div>
      </div>


      <div>
        <br />

        <div className="ion-padding">
          <h3 className="iontitle">{project?.projtitle}</h3>
          <br />
          <label>Objectif :</label>
          <br />
          <br />
          <p> {project?.des} </p>
          <br />
          <h4>Task To Do</h4>

          <div>
            {tasks.map((tasks: any) => (
              <div key={tasks.id}>
                <Tasks
                  id={tasks.id}
                  task={tasks.task}
                  projid={tasks.projid}
                  projcolor={tasks.projcolor}
                  onDelete={deleteChangeHandler}
                />
              </div>
            ))}
          </div>
        </div>



          <div className="ion-padding">
            <h4>Add task {project?.color}</h4>
            <div>
              <label position="floating">Task</label>
              <input
                ref={taskInputRef}
                onIonChange={(e: any) => settask(e.target.value)}
              />
            </div>
            <button
              onClick={pushtask}
              className="ion-margin-top"
              expand="block"
            >
              Add
            </button>
          </div>

      </div>
    </div>
  );
};

export default Project;
